mapper.select:
select p.*,CONCAT(d.name,'-',di.name,'-',dis.name) as arean,concat(c.name,'-',co.name,p.address) as areans from peoples p
  INNER JOIN districts d
  on p.idp=d.id
  INNER JOIN districts di
  on p.idc=di.id
  INNER JOIN districts dis
  on p.idt= dis.id
  INNER JOIN community c
  on p.eid=c.id
  INNER JOIN community co
  on p.xid=co.id
  where p.name like #{name}
   and p.sex like #{sex}
   and p.age > #{age1}
   and p.age  <  #{age2}
   and p.cause like #{cause}
   and jid like #{jid}

       and eid = #{eid}


      and xid = #{xid}

  limit #{index},10

select count(p.id)
 from peoples p
INNER JOIN districts d
on p.idp=d.id
INNER JOIN districts di
on p.idc=di.id
INNER JOIN districts dis
on p.idt= dis.id
INNER JOIN community c
on p.eid=c.id
INNER JOIN community co
on p.xid=co.id
where p.name like #{name}
and  p.sex like #{sex}
and p.age > #{age1}
and p.age  <   #{age2}
and p.cause like #{cause}
and jid like #{jid}

    and eid = #{eid}


    and xid = #{xid}
